import Foundation

final class ContactPresenter {

    // MARK: - Private properties -

    fileprivate let contact: ContactDisplayable

    private unowned let view: ContactViewInterface
    private let wireframe: ContactWireframeInterface

    // MARK: - Lifecycle -

    init(view: ContactViewInterface,
        contact: ContactDisplayable,
        wireframe: ContactWireframeInterface) {
        self.view = view
        self.contact = contact
        self.wireframe = wireframe
    }
    
    
}

// MARK: - Extensions -

extension ContactPresenter: ContactPresenterInterface {
    func viewDidLoad() {
        self.view.update(contact: contact)
    }
}
